}
.header-right-menu .dropdown-panel,
.header-right-menu .dropdown-menu {
  margin-top: -1px;
}
.header-right-menu .dropdown-panel-body {
  position: relative;
  padding-bottom: 40px;
}
.header-right-menu .panel_rows {
  max-height: 400px;
  overflow-y: scroll;
}
.header-right-menu .panel_actions {
  border-top: 1px solid #dddddd;
  position: absolute;
  left: 0;
  right: 0;
  width: 100%;
  bottom: 1px;
  line-height: 38px;
}
.header-right-menu .panel_actions a {
  color: #555555;
}
.header-right-menu .panel_actions a:hover {
  color: #297fc7;
}
@media (min-width: 768px) {
  .dropdown-panel {
    right: -60px;
  }
}
footer {
  position: relative;
  background: #1a1a1a;
  color: #6f8494;
  font-size: 13px;
  margin-top: 20px;
}
footer a {
  color: #6f8494;
}
footer a:hover,
footer a:active,
footer a:focus {
  color: #e4e8eb;
  text-decoration: none;
}
.footer-holder {
  background: #1a1a1a;
  padding: 40px 0;
}
footer .list-inline li + li :before {
  content: "\00b7";
  display: inline-block;
  padding-right: 10px;
}
@media (min-width: 480px) {
  footer .footer-menu {
    float: right;
  }
}
@media (max-width: 767px) {
  footer .footer-menu {
    margin-left: -5px;
    margin-right: -5px;
  }
}
@media (max-width: 767px) {
  footer .copyright {
    padding-bottom: 20px;
  }
}
#logo:hover {
  color: #ffffff;
  transition: all 0.5s ease;
}
#main_content {
  margin: 0;
}
#container #content > ._block_h1 > h1 {
  background: transparent;
  padding: 20px 0px 20px 0px;
  margin-bottom: 0;
  border-bottom: 3px #dbe1e4 solid;
  line-height: normal;
  font-weight: 300;
  font-size: 22px;
  color: #222222;
  letter-spacing: 1px;
}
#container #content > ._block_h1 > h1 > a {
  color: #222222;
  white-space: normal;
  max-width: 100%;
  display: block;
}
#container #content > ._block_h1 > h1 a:hover {
  color: #297fc7;
}
#pf-loading-message,
#mail-pf-loading-message {
  font-family: Lucida Grande, Helvetica Neue, Helvetica, Arial, sans-serif;
  text-align: center;
  font-size: 14px;
  color: #297fc7;
  position: fixed;
  z-index: 999999;
  left: 0;
  right: 0;
  top: 0;
  width: 100%;
  display: none;
}
#pf-loading-message span,
#mail-pf-loading-message span {
  display: block;
  margin: 0 auto;
}
#pf-loading-message span[class*="l-"],
#mail-pf-loading-message span[class*="l-"] {
  display: inline-block;
  background: #ee5454;
  width: 8px;
  height: 8px;
  -webkit-border-radius: 100%;
  -moz-border-radius: 100%;
  border-radius: 100%;
  margin: 2px 2px;
  position: relative;
  -webkit-animation: loader 4s infinite;
  -webkit-animation-timing-function: cubic-bezier(0.03, 0.615, 0.995, 0.415);
  -webkit-animation-fill-mode: both;
  -moz-animation: loader 4s infinite;
  -moz-animation-timing-function: cubic-bezier(0.03, 0.615, 0.995, 0.415);
  -moz-animation-fill-mode: both;
  -ms-animation: loader 4s infinite;
  -ms-animation-timing-function: cubic-bezier(0.03, 0.615, 0.995, 0.415);
  -ms-animation-fill-mode: both;
  animation: loader 4s infinite;
  animation-timing-function: cubic-bezier(0.03, 0.615, 0.995, 0.415);
  animation-fill-mode: both;
}
#pf-loading-message span.l-1,
#mail-pf-loading-message span.l-1 {
  -webkit-animation-delay: 1s;
  -moz-animation-delay: 1s;
  -ms-animation-delay: 1s;
  animation-delay: 1s;
}
#pf-loading-message span.l-2,
#mail-pf-loading-message span.l-2 {
  -webkit-animation-delay: 0.8s;
  -moz-animation-delay: 0.8s;
  -ms-animation-delay: 0.8s;
  animation-delay: 0.8s;
}
#pf-loading-message span.l-3,
#mail-pf-loading-message span.l-3 {
  -webkit-animation-delay: 0.6s;
  -moz-animation-delay: 0.6s;
  -ms-animation-delay: 0.6s;
  animation-delay: 0.6s;
}
#pf-loading-message span.l-4,
#mail-pf-loading-message span.l-4 {
  -webkit-animation-delay: 0.4s;
  -moz-animation-delay: 0.4s;
  -ms-animation-delay: 0.4s;
  animation-delay: 0.4s;
}
#pf-loading-message span.l-5,
#mail-pf-loading-message span.l-5 {
  -webkit-animation-delay: 0.2s;
  -moz-animation-delay: 0.2s;
  -ms-animation-delay: 0.2s;
  animation-delay: 0.2s;
}
#pf-loading-message span.l-6,
#mail-pf-loading-message span.l-6 {
  -webkit-animation-delay: 0s;
  -moz-animation-delay: 0s;
  -ms-animation-delay: 0s;
  animation-delay: 0s;
}
@-webkit-keyframes loader {
  0% {
    -webkit-transform: translateX(-200px);
    opacity: 0;
  }
  25% {
    opacity: 1;
  }
  50% {
    -webkit-transform: translateX(100px);
    opacity: 0;
  }
  100% {
    opacity: 0.1;
  }
}
@-moz-keyframes loader {
  0% {
    -moz-transform: translateX(-200px);
    opacity: 0;
  }
  25% {
    opacity: 1;
  }
  50% {
    -moz-transform: translateX(100px);
    opacity: 0;
  }
  100% {
    opacity: 0.1;
  }
}
@-ms-keyframes loader {
  0% {
    -ms-transform: translateX(-200px);
    opacity: 0;
  }
  25% {
    opacity: 1;
  }
  50% {
    -ms-transform: translateX(100px);
    opacity: 0;
  }
  100% {
    opacity: 0.1;
  }
}
@keyframes loader {
  0% {
    transform: translateX(-200px);
    opacity: 0;
  }
  25% {
    opacity: 1;
  }
  50% {
    transform: translateX(100px);
    opacity: 0;
  }
  100% {
    opacity: 0.1;
  }
}
body.page-loading #pf-loading-message,
body.page-loading #mail-pf-loading-message {
  display: block;
}
