//== Mixin
.fa(@font) {
    display: inline-block;
    font-family: "FontAwesome";
    font-style: normal;
    font-weight: normal;
    line-height: inherit;
    font-size: inherit;
    text-rendering: auto;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    transform: translate(0, 0);
    text-indent: 0 !important;
    content: @font;
}

.border-radius(@radius) {
    -moz-border-radius: @radius;
    -webkit-border-radius: @radius;
    border-radius: @radius;
}

.truncateLines(@font-size, @line-height, @lines, @fixHeight) {
    max-width: 100%;
    display: block;
    white-space: normal;
    overflow: hidden;
    padding: 0;
    font-size: @font-size;
    text-overflow: ellipsis;
    line-height: @line-height;
    & when (@fixHeight) {
        height: @line-height * @lines;
    }

    & when not (@fixHeight) {
        max-height: @line-height * @lines;
    }
    -webkit-line-clamp: @lines;
    -webkit-box-orient: vertical;
}

.pt-base {
    padding-top: @padding-base-vertical;
}

.pb-base {
    padding-bottom: @padding-base-vertical;
}

.pv-base {
    padding-top: @padding-base-vertical;
    padding-bottom: @padding-base-vertical;
}

.lines-base-3 {
    .truncateLines(@font-size-base, 20px, 3, false);
}

.lines-base-3-forced {
    .truncateLines(@font-size-base, 20px, 3, true);
}

.lines-base-1 {
    .truncateLines(@font-size-base, 20px, 1, false);
}

.lines-base-1-forced {
    .truncateLines(@font-size-base, 20px, 1, true);
}

.lines-base-2 {
    .truncateLines(@font-size-base, 20px, 2, false);
}

.lines-base-2-forced {
    .truncateLines(@font-size-base, 20px, 2, true);
}

.no_transition() {
    -moz-transition: none;
    -webkit-transition: none;
    -o-transition: color 0 ease-in;
    transition: none;
}

.reset() {
    background: transparent;
    border-radius: 0;
    box-shadow: none;
    color: inherit;
    height: auto;
    line-height: normal;
    position: static;
    margin: 0;
    padding: 0;
    border: 0;
    font: inherit;
    font-size: 100%;
    vertical-align: baseline;
    width: auto;
    float: none;
    text-indent: 0;
}

.button(@bg: @brand-primary, @color: @brand-primary) {
    display: block;
    text-align: center;
    border: 1px @bg solid;
    height: 30px;
    line-height: 30px;
    padding: 0 10px;
    text-transform: uppercase;
    font-weight: 300;
    font-size: (@font-size-base - 1) !important;
    letter-spacing: 1px;
    color: @color;
}

.button_hover() {
    border-color: darken(@brand-primary, 10%);
    background: @brand-primary;
    color: @brand-primary-color;
    transition: @transition;
}

.nav() {
    ul {
        background: @block-bg;
    }

    ul li a, ul li a:hover {
        display: block;
        background: transparent;
        font-weight: 300;
        font-size: @font-size-base;
        height: 35px;
        line-height: 35px;
        padding: 0;
        color: lighten(@text-color, 10%);
        text-indent: 10px;
        border-radius: 0;
        margin: 0;
    }

    ul li a:hover {
        transition: @transition;
        color: darken(@text-color, 50%);
    }

    ul li.active a, ul li.active a:hover {
        background: @brand-primary;
        color: @brand-primary-color;
    }
}

.full_input() {
    background: @form-bg;
    color: @form-color;
    border: 0;
    padding: 5px 0;
    width: 100%;
    outline: none;
    text-indent: 0;
}

.block_menu() {
    > li {
        display: block;
        margin: 0;
        > a {
            padding: 14px 15px 14px 10px;
            color: @link-focus;
            border-bottom: 1px darken(@block-bg, 6%) solid;
        }
        > a:hover {
            background: darken(@block-bg, 6%);
            color: @link-focus;
        }
    }

    li.active {
        > a, > a:hover {
            background: @brand-primary;
            color: @brand-primary-color;
        }
    }
}

.categories(@ele) when (@hover-categories) {
    #left, #right {
        #@{ele} {
            border: 1px darken(@block-bg, 14%) solid;

            > .title {
                text-transform: uppercase;
                background: @block-bg;
                color: @block-color;
                font-size: @font-size-base;
                border: 0;
                height: 40px;
                line-height: 40px;
                position: relative;
                padding: 0;
                text-indent: 50px;
            }

            > .title:before {
                .fa('\f0c9');
                position: absolute;
                left: 0;
                top: 0;
                width: 40px;
                height: 40px;
                background: darken(@block-bg, 8%);
                z-index: 1;
                text-align: center;
                font-size: 20px;
                text-indent: 0;
            }

            > .content {
                display: none;
                max-height: 250px;
                overflow: auto;
                background: @block-bg;
                padding: 10px;
            }
        }

        #@{ele}:hover {
            border: 1px @brand-primary solid;
            box-shadow: @block-box-shadow-light;

            > .title:before {
                background: darken(@brand-primary, 5%);
            }

            > .title:after {
                color: @brand-primary-color;
            }

            > .title {
                background: @brand-primary;
                color: @brand-primary-color;
            }

            > .content {
                display: block;
                transition: @transition;
            }
        }
    }
}

.threeColumn(@id) {

    #@{id} {
        #panels {
            position: static;
            width: auto;
        }

        #left {
            position: absolute;
            left: -(@column-left-width + 20);
            top: 0;
            width: @column-left-width;
        }

        #right {
            position: absolute;
            right: 0;
            top: 0;
            width: @column-width;
        }
    }
}

.noHeader(@id) {
}

.fullContent(@id) {
    #@{id} {
        #content {
            width: 100%;
        }
    }
}

.userImage(@size) {
    @newSize: @size * 1px;
    ._image__@{size}_square {
        width: @newSize;
        height: @newSize;
        overflow: hidden;
        background-size: contain;
    }

    ._image_@{size} {
        width: @newSize;
        height: @newSize;
        overflow: hidden;
        background-size: contain;
    }
}

.loopingClass(@className, @index) when (@index > 0) {

    @newIndex: (@index * 10);
    @total: percentage((@index) * 0.1);
    @padding: ((@block-content-padding / 2) * 2);

    .@{className}-@{newIndex} {
        width: ~"calc(@{total} - @{padding})";
        float: left;
        vertical-align: top;
        word-wrap: break-word;
        padding: (@block-content-padding / 2);
    }

    .loopingClass(@className, (@index - 1));
}

.loopingClass (@className, 0) {
}

.loopingClassBrand(@className, @index) when (@index > 0) {

    @newIndex: (@index * 10);

    .@{className}-@{newIndex} {
        & when (@className = margin) {
            margin: @newIndex + 0;
        }
        & when (@className = margin-bottom) {
            margin-bottom: @newIndex + 0;

        }
    }

    .loopingClassBrand(@className, (@index - 1));
}

/**
	generate button icons
**/
.btn_cmd_icon(@icon, @size, @font-size, @active_class) {
    & {
        display: block;
        background: darken(@block-bg, 8%);
        color: darken(@block-bg, 26%);
        width: @size;
        height: @size;
    }

    &:hover {
        background: @brand-primary;
        color: @brand-primary-color;
        transition: @transition;
    }

    &:before {
        .fa(@icon);
        text-align: center;
        width: @size;
        height: @size;
        font-size: @font-size;
        line-height: @size;
    }
    &:hover:before {
        color: @brand-primary-color;
        transition: @transition;
    }

    &.@{active_class} {
        color: @brand-primary;
    }
}

.absolute_cmd_icon(@icon, @size, @font-size) {
    & {
        display: block;
        overflow: hidden;
        position: absolute;
        text-indent: -1000px;
        background: darken(@block-bg, 8%);
        color: darken(@block-bg, 26%) !important;
        width: @size;
        height: @size;
    }

    &:hover {
        background: @brand-primary;
        color: @brand-primary-color;
        transition: @transition;
    }
    &:before {
        .fa(@icon);
        position: absolute;
        left: floor((@size /2- @font-size/2));
        top: 0;
        font-size: @font-size;
        line-height: @size;
    }
    &:hover:before {
        color: @brand-primary-color;
        transition: @transition;
    }
}

