    font-style: normal;
    font-weight: normal;
    letter-spacing: normal;
    line-break: auto;
    line-height: @line-height-base;
    text-align: left; // Fallback for where `start` is not supported
    text-align: start;
    text-decoration: none;
    text-shadow: none;
    text-transform: none;
    white-space: normal;
    word-break: normal;
    word-spacing: normal;
    word-wrap: normal;
}

// Typography

.text-emphasis-variant(@color) {
    color: @color;
    a&:hover,
    a&:focus {
        color: darken(@color, 10%);
    }
}

// Text overflow
// Requires inline-block or block for proper styling

.text-overflow() {
    overflow: hidden;
    text-overflow: ellipsis;
    white-space: nowrap;
}

