    #content > ._block_h1 > h1 {
        background: transparent;
        padding: @block-content-padding 0px @block-content-padding 0px;
        margin-bottom: 0;
        border-bottom: 3px darken(@body-bg, 3%) solid;
        line-height: normal;
        font-weight: 300;
        font-size: @font-size-base + 8;
        color: @link-focus;
        letter-spacing: 1px;

        > a {
            color: @link-focus;
            white-space: normal;
            max-width: 100%;
            display: block;
        }

        a:hover {
            color: @link-focus-hover;
        }
    }
}
@pf-loading-left: 200px;
@pf-loading-right: 100px;

#pf-loading-message {
    font-family: Lucida Grande, Helvetica Neue, Helvetica, Arial, sans-serif;
    text-align: center;
    font-size: 14px;
    color: @brand-primary;
    position: fixed;
    z-index: 999999;
    left: 0;
    right: 0;
    top: 0;
    width: 100%;
    display: none;
    span {
        display: block;
        margin: 0 auto;
    }

    span[class*="l-"] {
        display: inline-block;
        background: @brand-danger;
        width: 8px;
        height: 8px;

        -webkit-border-radius: 100%;
        -moz-border-radius: 100%;
        border-radius: 100%;

        margin: 2px 2px;
        position: relative;

        -webkit-animation: loader 4s infinite;
        -webkit-animation-timing-function: cubic-bezier(0.030, 0.615, 0.995, 0.415);
        -webkit-animation-fill-mode: both;
        -moz-animation: loader 4s infinite;
        -moz-animation-timing-function: cubic-bezier(0.030, 0.615, 0.995, 0.415);
        -moz-animation-fill-mode: both;
        -ms-animation: loader 4s infinite;
        -ms-animation-timing-function: cubic-bezier(0.030, 0.615, 0.995, 0.415);
        -ms-animation-fill-mode: both;
        animation: loader 4s infinite;
        animation-timing-function: cubic-bezier(0.030, 0.615, 0.995, 0.415);
        animation-fill-mode: both;
    }

    span.l-1 {
        -webkit-animation-delay: 1s;
        -moz-animation-delay: 1s;
        -ms-animation-delay: 1s;
        animation-delay: 1s;
    }
    span.l-2 {
        -webkit-animation-delay: 0.8s;
        -moz-animation-delay: 0.8s;
        -ms-animation-delay: 0.8s;
        animation-delay: 0.8s;
    }
    span.l-3 {
        -webkit-animation-delay: 0.6s;
        -moz-animation-delay: 0.6s;
        -ms-animation-delay: 0.6s;
        animation-delay: 0.6s;
    }
    span.l-4 {
        -webkit-animation-delay: 0.4s;
        -moz-animation-delay: 0.4s;
        -ms-animation-delay: 0.4s;
        animation-delay: 0.4s;
    }
    span.l-5 {
        -webkit-animation-delay: 0.2s;
        -moz-animation-delay: 0.2s;
        -ms-animation-delay: 0.2s;
        animation-delay: 0.2s;
    }
    span.l-6 {
        -webkit-animation-delay: 0s;
        -moz-animation-delay: 0s;
        -ms-animation-delay: 0s;
        animation-delay: 0s;
    }

    @-webkit-keyframes loader {
        0% {
            -webkit-transform: translateX(-1 * @pf-loading-left);
            opacity: 0;
        }
        25% {
            opacity: 1;
        }
        50% {
            -webkit-transform: translateX(@pf-loading-right);
            opacity: 0;
        }
        100% {
            opacity: 0.1;
        }
    }
    @-moz-keyframes loader {
        0% {
            -moz-transform: translateX(-1*@pf-loading-left);
            opacity: 0;
        }
        25% {
            opacity: 1;
        }
        50% {
            -moz-transform: translateX(@pf-loading-right);
            opacity: 0;
        }
        100% {
            opacity: 0.1;
        }
    }
    @-ms-keyframes loader {
        0% {
            -ms-transform: translateX(-1*@pf-loading-left);
            opacity: 0;
        }
        25% {
            opacity: 1;
        }
        50% {
            -ms-transform: translateX(@pf-loading-right);
            opacity: 0;
        }
        100% {
            opacity: 0.1;
        }
    }
    @keyframes loader {
        0% {
            transform: translateX(-1* @pf-loading-left);
            opacity: 0;
        }
        25% {
            opacity: 1;
        }
        50% {
            transform: translateX(@pf-loading-right);
            opacity: 0;
        }
        100% {
            opacity: 0.1;
        }
    }
}

body.page-loading {
    #pf-loading-message {
        display: block;
    }
}

