/**
* Item action bar
*/
.item_bar {
    padding: 4px 0 12px 0;
    position: relative;
}

a.item_bar_approve,
a.item_bar_approve:hover {
    display: block;
    background: #D0D0D0;
    line-height: 24px;
    padding: 0 5px 0 5px;
    text-decoration: none;
    -moz-border-radius: 6px;
    -webkit-border-radius: 6px;
    border-radius: 6px;
    font-weight: bold;
    color: #333;
}

a.item_bar_approve_image,
a.item_bar_approve_image:hover {
    background: none;
}

.item_bar ul {
    left: auto;
    right: 0;
}

a.item_bar_action,
a.item_bar_action:hover {
    background: #fff url('@{image-folder}/layout/item_bar_action.png') no-repeat;
    display: block;
    line-height: 24px;
    text-decoration: none;
    color: #fff;
    text-align: left;
    width: 140px;
    font-weight: bold;
}

a.item_bar_action_clicked,
a.item_bar_action_clicked:hover {
    background: #fff url('@{image-folder}/layout/item_bar_action.png') no-repeat 0 -24px;
    color: #333;
}

a.item_bar_action span {
    padding: 0 0 0 10px;
}

.item_bar_image {
    vertical-align: middle;
    padding-bottom: 3px;
