.item_image_content {
    padding-left: 5px;
}

.item_content img.parsed_image {
    border: 1px #ccc solid;
    -moz-box-shadow: 6px 6px 6px #CFCFCF;
    -webkit-box-shadow: 6px 6px 6px #CFCFCF;
    box-shadow: 6px 6px 6px #CFCFCF;
