.colpick_new_color {
    position: absolute;
    left: 207px;
    top: 6px;
    width: 60px;
    height: 27px;
    background: #f00;
    border: 1px solid #8f8f8f;
}

/*Current color box*/
.colpick_current_color {
    position: absolute;
    left: 277px;
    top: 6px;
    width: 60px;
    height: 27px;
    background: #f00;
    border: 1px solid #8f8f8f;
}

/*Input field containers*/
.colpick_field, .colpick_hex_field {
    position: absolute;
    height: 20px;
    width: 60px;
    overflow: hidden;
    background: #f3f3f3;
    color: #b8b8b8;
    font-size: 12px;
    border: 1px solid #bdbdbd;
    -webkit-border-radius: 3px;
    -moz-border-radius: 3px;
    border-radius: 3px;
}

.colpick_rgb_r {
    top: 40px;
    left: 207px;
}

.colpick_rgb_g {
    top: 67px;
    left: 207px;
}

.colpick_rgb_b {
    top: 94px;
    left: 207px;
}

.colpick_hsb_h {
    top: 40px;
    left: 277px;
}

.colpick_hsb_s {
    top: 67px;
    left: 277px;
}

.colpick_hsb_b {
    top: 94px;
    left: 277px;
}

.colpick_hex_field {
    width: 68px;
    left: 207px;
    top: 121px;
}

/*Text field container on focus*/
.colpick_focus {
    border-color: #999;
}

/*Field label container*/
.colpick_field_letter {
    position: absolute;
    width: 12px;
    height: 20px;
    line-height: 20px;
    padding-left: 4px;
    background: #efefef;
    border-right: 1px solid #bdbdbd;
    font-weight: bold;
    color: #777;
}

/*Text inputs*/
.colpick_field input, .colpick_hex_field input {
    position: absolute;
    right: 11px;
    margin: 0;
    padding: 0;
    height: 20px;
    line-height: 20px;
    background: transparent;
    border: none;
    font-size: 12px;
    font-family: Arial, Helvetica, sans-serif;
    color: #555;
    text-align: right;
    outline: none;
}

.colpick_hex_field input {
    right: 4px;
}

/*Field up/down arrows*/
.colpick_field_arrs {
    position: absolute;
    top: 0;
    right: 0;
    width: 9px;
    height: 21px;
    cursor: n-resize;
}

.colpick_field_uarr {
    position: absolute;
    top: 5px;
    width: 0;
    height: 0;
    border-left: 4px solid transparent;
    border-right: 4px solid transparent;
    border-bottom: 4px solid #959595;
}

.colpick_field_darr {
    position: absolute;
    bottom: 5px;
    width: 0;
    height: 0;
    border-left: 4px solid transparent;
    border-right: 4px solid transparent;
    border-top: 4px solid #959595;
}

/*Submit/Select button*/
.colpick_submit {
    position: absolute;
    left: 207px;
    top: 149px;
    width: 130px;
    height: 22px;
    line-height: 22px;
    background: #efefef;
    text-align: center;
    color: #555;
    font-size: 12px;
    font-weight: bold;
    border: 1px solid #bdbdbd;
    -webkit-border-radius: 3px;
    -moz-border-radius: 3px;
    border-radius: 3px;
}

.colpick_submit:hover {
    background: #f3f3f3;
    border-color: #999;
    cursor: pointer;
}

/*full layout with no submit button*/
.colpick_full_ns .colpick_submit, .colpick_full_ns .colpick_current_color {
    display: none;
}

.colpick_full_ns .colpick_new_color {
    width: 130px;
