    top: 42px;
}

.colpick_full_ns .colpick_rgb_g, .colpick_full_ns .colpick_hsb_s {
    top: 73px;
}

.colpick_full_ns .colpick_rgb_b, .colpick_full_ns .colpick_hsb_b {
    top: 104px;
}

.colpick_full_ns .colpick_hex_field {
    top: 135px;
}

/*rgbhex layout*/
