    width: 68px;
}

.colpick_rgbhex .colpick_new_color {
    width: 34px;
    border-right: none;
}

.colpick_rgbhex .colpick_current_color {
    width: 34px;
    left: 240px;
    border-left: none;
}

/*rgbhex layout, no submit button*/
