    line-height: @line-height-base;
}

a, a:hover {
    color: @link-color;
    text-decoration: none;
}

a:hover {
    color: @link-hover-color;
    text-decoration: @link-hover-decoration;
}

.fa:hover {
    transition: @transition;
}

ul, ol {
    padding: 0;
    margin: 0;
    list-style: none;
}

li {
    padding: 0;
    margin: 0;
}

.holder {
    width: @container-max-width;
}

#logo, #logo:hover {
    width: @nav-width;
    height: @header-height;
    line-height: @header-height;
    text-indent: 45px;
    font-weight: 200;
    color: @header-color;
    background: darken(@header-bg, 10%);
    letter-spacing: 2px;
    position: fixed;
    left: 0;
    top: 0;
    z-index: 401;
    overflow: hidden;

    > i {
        position: absolute;
        left: 8px;
        top: 10px;
        width: 20px;
        height: 20px;
        background-size: contain;
    }

    > span > i {
        background: url(@logoUrl);
        background-size: cover;
        background-repeat: no-repeat;
        display: block;
        width: 30px;
        height: 30px;
        position: absolute;
        left: 5px;
        top: 5px;
        text-indent: 0;
    }
}

#panels.can_be_fixed {
    position: fixed;
}

#panels {
    width: @column-width;
    position: relative;
    display: table-cell;
    vertical-align: top;

    #right, #left {
        border: 0;
    }
}

.right_is_fixed {
    #panels {
        position: fixed;
        bottom: 5px;
        top: auto;
        right: auto;
        z-index: 100;
    }
}

