.mail_image {
    position: absolute;
    left: 25px;
    width: 50px;
    height: 50px;
}

.mail_content {
    @media (max-width: 700px) {
        min-width: 150px;
    }
    @media (max-width: 390px) {
        min-width: 100px;
    }
    margin-left: 85px;
    position: relative;
    min-width: 250px;
}

.mail_action {
    position: absolute;
    right: 4px;
    bottom: 12px;
    font-size: 10px;
    color: #808080;

    li {
        float: left;
        height: 16px;
        line-height: 16px;
        padding-left: 6px;
    }
}

.mail_link {
    font-weight: bold;
    font-size: 13px;
}

.mail_preview {
    padding: 5px 0 0 0;
    color: #808080;
}

.mail_delete,
.mail_delete:hover,
.mail_read,
.mail_read:hover {
    display: block;
    width: 16px;
    height: 16px;
    position: relative;
    overflow: hidden;
    text-indent: -1000px;
}

.mail_delete {
    background: url('@{image-folder}layout/action_delete.png') no-repeat;

    &:hover {
        background-color: #333;
    }
}

.mail_read {
    background: url('@{image-folder}layout/mail_read.png') no-repeat;

    &:hover {
        background-color: #333;
    }
}

.mail_is_new {
    background: #EDEFF4;
}

.mail_moderation {
    position: absolute;
    left: 2px;
    top: 20px;
}

#js_mail_textarea #message {
    height: 30px;
}

.mail_next_prev {
    position: absolute;
    right: 0;
    background: #f9f9f9;
    border: 1px #dfdfdf solid;

    -moz-box-shadow: 3px 3px 3px #f9f9f9;
    -webkit-box-shadow: 3px 3px 3px #f9f9f9;
    box-shadow: 3px 3px 3px #f9f9f9;

    ul li {
        a, a:hover {
            display: block;
            width: 30px;
            height: 30px;
            line-height: 50px;
            overflow: hidden;
            text-indent: -1000px;
            position: relative;
            float: left;
            margin: 0;
            padding: 0;
            background: transparent;
        }

        &.next_message a, &.next_message a:hover {
            border-left: 1px #dfdfdf solid;
            background: url('@{image-folder}layout/next_message.png') no-repeat;
        }

        &.previous_message a, &.previous_message a:hover {
            background: url('@{image-folder}layout/previous_message.png') no-repeat;
        }

        &.next_message a:hover, &.previous_message a:hover {
            background-color: #f1f1f1;
        }
    }
}

.mail_thread_holder {
    position: relative;

    .mail_action {
        top: 0;
    }

}

#js_mail_thread_current_cnt {
    display: none;
}

.mail_thread_user {
    font-weight: bold;
    padding-bottom: 4px;
    width: 85%;
    overflow: hidden;
}

.mail_thread_time {
    position: absolute;
    right: 0;
    top: 0;
}

#js_main_mail_thread_holder .global_attachment {
    border-top: 0;
}

div.is_last_submessage {
    border-bottom: 0;
}

.mail_thread_forward {
    border-left: 3px #EFEFEF solid;
    padding-left: 5px;
    margin-top: 5px;

    .row_title_image {
        width: 36px;
    }

    .row_title_info {
        margin-left: 42px;
    }

}

#js_main_mail_thread_holder a.global_view_more,
#js_main_mail_thread_holder a.global_view_more:hover {
    margin-top: 0;
}

#js_main_mail_thread_holder .item_bar {
    padding-bottom: 0;
}

#mail-pf-loading-message {
    &:extend(#pf-loading-message all);

    position: relative;
}
.profiles_second_nav {
    padding: 0 @block-content-padding;
    background: @block-bg;
    margin: 0 @grid-gutter-width/2;

    &:after {
        clear: both;
        content: " ";
        display: block;
    }
    > ul {

    }
    > ul > li {
        float: left;
    }
    > ul > li.pull-right {
        float: right;
    }

    > ul > li,
    > ul > li > a {
        display: block;
        position: relative;
        color: @link-color;
    }
    > ul > li > a:hover {
        color: @link-hover-color;
    }
    > ul > li > a {
        line-height: @navbar-height;
        padding: 0 10px;
    }
}

#page_pages_add {
    #js_pages_add_holder {
        > .extra_info, > form > strong, > form > select {
            display: none;
        }
    }

    .pages_type_add_form_holder {
        padding-top: 0;
    }

    input.pages_type_add_input {
        width: 100%;
    }

    .pages_type_add_holder {
        text-align: left;
        background: @block-bg;
        border-bottom: 1px darken(@block-bg, 5%) solid;
    }

    a.pages_type_add_inner_link, a.pages_type_add_inner_link:hover {
        text-align: left;
        display: block;
        padding: @block-content-padding;
        background: @block-bg;
        color: @block-color;
    }

    a.pages_type_add_inner_link:hover {
        background: darken(@block-bg, 2%);
        color: darken(@block-color, 5%);
    }

    a.pages_type_add_inner_link.focus {
        background: @brand-primary;
        color: @brand-primary-color;
    }

    a.pages_type_add_inner_link.unfocus {
        opacity: 0.4;
    }

    .pages_type_add_form {
        padding: 10px;
        background: darken(@block-bg, 6%);
        display: none;

        .table {
            background: transparent;
            padding: 0;
        }
    }

    #js_pages_block_info {
        background: @block-bg;
        padding: 10px;

        div.table_clear {
            padding-top: 10px;
        }
    }
}

#page_pages_index {
    .pages_item{
        margin-bottom: 10px;
        margin-top: 10px;
    }
    .user_rows {
        display: block;
        float: left;
    }

}

.pages_header {
    position: relative;
    width: 100%;
    margin: auto;
    height: auto;
    min-height: 140px;

    .pages_header_cover {
        overflow: hidden;
        img {
            background-size: cover;
            width: 100%;
        }
        .ui-draggable {
            cursor: move;
        }
        #save_reposition_cover {
            position: absolute;
            bottom: 10px;
            right: 30px;
        }
    }
}

.pages_like_join, .js_claim_page {
    .btn;
    .btn-primary;
}

.pages_header_info {
    background: rgba(0, 0, 0, 0.6);
    padding: 10px 0;
    position: absolute;
    z-index: 100;
    top: 0;
    right: 0;
    left: 0;
    min-height: 100px;
    display: -webkit-flex;
    display: flex;
    -webkit-flex-direction: row;
    flex-direction: row;

    .pages_header_name {
        padding-top: 5px;
        padding-bottom: 5px;
        display: block;

        h1 {
            color: #fff;
            line-height: normal;
            padding: 0;
            margin: 0;
            white-space: nowrap;
            overflow: hidden;
            text-overflow: ellipsis;
            max-width: 600px;
        }

        ul {
            margin-top: 1px;
            margin-bottom: 5px;

            li {
                color: darken(#f1f1f1, 10%);
                font-size: @font-size-base - 1;
                font-weight: 300;
                display: inline-block;
            }

            li:first-of-type:after {
                content: ' - ';
            }
        }
    }

    > div {
        &:first-child {
            max-width: 120px;
            min-width: 100px;
        }
        &:nth-child(2) {
            overflow: hidden;
        }
        vertical-align: top;
        margin-left: 10px;
        margin-bottom: -6px;
        -webkit-flex: auto;
        flex: auto;
    }

    .pages_header_image {
        min-width: 100px;
        min-height: 100px;
        background: #fff;

        img {
            min-width: 100px;
            min-height: 100px;
            background-size: cover;
        }
    }
}

.pages_menu {
    position: absolute;
    right: 20px;
    bottom: 15px;
    z-index: 10;

    > ul {
        li {
            display: inline-block;
            margin-left: 10px;

            a, a:hover {
                color: darken(#fff, 20%);
                font-weight: 200;
                font-size: @font-size-base + 3;
                text-transform: lowercase;
                letter-spacing: 1px;
            }

            a:hover {
                color: #fff;
                transition: @transition;
            }
        }
    }
}

.pages_header {
    background: transparent;
    margin: auto;
}

.pages_header_cover {
    display: none;
    max-height: 420px;
    @media (max-width: @screen-xs-min) {
        max-height: 210px;
    }
}

#page_pages_view, ._is_pages_view {
    .pages_header_cover {
        display: block;
    }

    .pages_header {
        background: inherit;
        margin: auto;
    }

    #panels, #content {
        vertical-align: top;
    }

    #container {
        display: table;
    }

    #content {
        margin-left: 0;
    }

    #panels {
        position: static;
    }
}

._is_pages_view {
    .breadcrumbs_menu, .profiles_second_breadcrumbs {
        position: absolute;
        right: 20px;
        top: 20px;
        z-index: 200;
        bottom: auto;
        min-height: 30px;
    }

    div.header_bar_menu {
        margin: auto;
    }

    #activity_feed_share_this_one ul li div.parent {
        .reset();
    }

    #activity_feed_share_this_one ul li div.parent select {
        width: 100%;
        background: darken(@block-bg, 8%);
        padding: 10px;
        font-size: @font-size-base - 2;
        height: 25px;
    }

    #activity_feed_share_this_one {
        position: static;
        margin-bottom: -10px;
    }

    #header {
        display: none;
    }

    #top {
        background: transparent;
        margin-top: 20px;
    }

    .item_bar {
        display: block;
        top: auto;
        bottom: 45px;
        right: 10px;
        left: auto;
        z-index: 100;
        overflow: visible;
    }
}

.pages_edit_widget_tools {
    position: absolute;
    left: auto;
    right: 0;
}

.pages_edit_widget li.widget {
    position: relative;
    padding-left: 0;
}

.nav_pages a, .nav_pages a:hover {
    vertical-align: top;
}

#js_block_border_pages_profile {
    ._size__120 > span, ._size__120:hover > span {
        top: 22px;
    }
}

#js_is_user_profile #js_is_page #section_menu {
    top: 10px;
}

.pages_edit_widget li.widget {
    border-bottom: 1px #dfdfdf solid;
    padding: 10px 0 10px 40px;
    position: relative;
}

.pages_edit_widget_tools {
    position: absolute;
    left: 0;
    top: 0;
}

.pages_create_new_widget {
    padding-bottom: 10px;
}

.pages_create_new_widget a,
.pages_create_new_widget a:hover {
    font-size: 13px;
}

#page_friend_profile {
    .profiles_banner {
        .breadcrumbs_menu {
            bottom: 10px;
        }
    }
}

#_privacy_holder_table .table {
    margin-bottom: 0;
}

.item_view_content {
    word-break: break-all;
    word-wrap: break-word;
}

#js_pages_like_join_holder, #js_block_border_pages_profile {
    .user_rows_mini .user_rows {
        width: 79px;
        height: 110px;
    }
}

@media (max-width: @screen-sm) {
    #page_pages_view {
        #activity_feed_share_this_one > ul > li {
            position: relative;
        }
        #activity_feed_share_this_one > ul > li a.btn {
            display: block;
            border: 0
        }
        #activity_feed_share_this_one {
            float: none;
            display: block;
            margin-bottom: 0;
            border: 0;
        }
        .activity_feed_form_button_position_button {
            display: block;
            float: none;
        }
        #activity_feed_submit {
            display: block;
            width: 100%;
        }
    }
}

#js_add_location_suggestions {
    background-color: @block-bg;
    position: absolute;
    border: 1px solid rgba(0, 0, 0, 0.15);
    & when (@use-border-radius) {
        border-radius: 4px;
    }
    box-shadow: 0 6px 12px rgba(0, 0, 0, 0.175);
    background-clip: padding-box;

    /* Wrapper for locations in the feed*/
    .js_div_place {
        width: 100%;
        padding: 10px;
    }

    .js_div_place:hover {
        background-color: darken(@block-bg, 4%);
        cursor: pointer;
    }

    .js_div_place_name, .js_div_place_vicinity {
        display: inline-block;
    }
}

#js_vanity_url_new {
    display: inline-block;
    width: auto;
}
.profiles_banner_bg {
    > .cover_bg {
        position: absolute;
        top: 0;
        left: 0;
        right: 0;
        bottom: 0;
        background: rgba(0, 0, 0, 0.4);
    }
    .cover {
        display: block;
        overflow: hidden;
        max-height: 420px;
        .ui-draggable {
            cursor: move;
        }
    }
    #save_reposition_cover {
        position: absolute;
        bottom: @block-content-padding;
        right: @block-content-padding;
    }
    h1 a, h1 a:hover, .h1 a, .h1 a:hover {
        color: #fff;
    }

    .profiles_extra_info {
        color: darken(#fff, 10%) !important;
        font-weight: 400 !important;
        text-shadow: 0 2px 2px rgba(0, 0, 0, 0.5);
    }
}

.profiles_banner {
    min-height: 250px;
    max-height: 500px;
    position: relative;
    background: lighten(#0c0c0c, 10%);

    .cover > img {
        background-size: cover;
        width: 100%;
    }

    h1 a, h1 a:hover, .h1 a, .h1 a:hover {
        color: #fff;
    }

    .profiles_extra_info {
        color: darken(#fff, 25%);

        @media (max-width: @screen-xs) {
            overflow: hidden;
            text-overflow: ellipsis;
            white-space: nowrap;
        }
    }
}

#change_cover_function {
    position: absolute;
    top: 15px;
    left: 40px;
    .fa-camera {
        :hover {
            font-size: 30px;
        }
        font-size: 25px;
        color: @header-bg;
    }
}

.profile_image {
    background: @block-bg;
    border: 2px darken(@block-bg, 10%) solid;
    border-radius: @border-radius-base;
    position: absolute;
    left: 20px;
    bottom: -20px;
    @media (max-width: @screen-xs) {
        height: 80px;
        width: 80px;
    }
    width: 160px;
    height: 160px;
    overflow: hidden;
    z-index: 4;

    img {
        width: 100%;
        height: 100%;
        background-size: contain;
    }

    > .p_4 {
        display: none;

        input {
            position: absolute;
            top: 0;
            left: 0;
            right: 0;
            height: 100%;
            opacity: 0;
            z-index: 2;
        }
    }

    > .p_4 > span {
        display: block;
        position: absolute;
        overflow: hidden;
        text-indent: -1000px;
        background: rgba(0, 0, 0, 0.4);
        color: #fff;
        right: 0;
        left: 0;
        bottom: 0;
        z-index: 1;
        height: 30%;
    }

    > .p_4 > span:before {
        .fa('\f093');
        position: absolute;
        left: 50%;
        top: 50%;
        font-size: 30px;
        margin-left: @block-content-padding*-1;
        margin-top: @block-content-padding*-1;
    }
}

.profile_menu_is_fixed.panel_is_active {
    .profiles_menu, .profile_image {
        z-index: 298;
    }
}

.profiles_menu {
    background: @header-bg;
    height: @header-height;
    position: relative;

    span.badge_number {
        position: absolute;
        right: 5px;
        top: 12px;
        font-size: @font-size-base - 4;
        display: inline-block;
        background: darken(@block-bg, 5%);
        height: auto;
        line-height: normal;
        padding: 4px 6px;
    }

    li.active span.badge_number {
        background: darken(@block-bg, 20%);
    }

    > ul {
        position: relative;
        padding-left: 180px;
        > li {
            vertical-align: top;
            border-bottom: 3px transparent solid;
            display: inline-block;
            height: @header-height;
            line-height: @header-height;
            margin-right: -2px;
            position: relative;

            > a.explore, > a.explore:hover {
                font-size: 24px;
                border-left: 2px darken(@header-bg, 6%) solid;
            }

            > a.explore:hover {
                background: @brand-primary;
                color: @brand-primary-color;

                i:hover {
                    color: @brand-primary-color;
                }
            }

            > a, > a:hover {
                display: block;
                color: darken(@header-color, 20%);
                padding: 0 20px;
                font-size: @font-size-base + 1;
                font-weight: 300;
                letter-spacing: 1px;

                > span {
                    font-size: @font-size-base - 4;
                    display: inline-block;
                    vertical-align: middle;
                    margin-left: 8px;
                    margin-bottom: 2px;
                    background: darken(@header-bg, 5%);
                    height: auto;
                    line-height: normal;
                    padding: 4px 6px;
                }
            }
        }

        > li:hover {
            border-color: @brand-primary;
            transition: @transition;

            > a, > a:hover {
                color: lighten(@header-color, 10%);
            }

            > a.explore, > a.explore:hover {
                background: @brand-primary;
                color: @brand-primary-color;
            }

            > ul {
                display: block;
            }
        }

        > li.active {
            border-color: @brand-primary;
        }
    }
}

.profiles_info {
    position: absolute;
    max-width: 100%;
    bottom: 30px;
    @media (max-width: @screen-xs) {
        left: 0;
        text-align: center;
        width: 100%;
        padding: 0 @block-content-padding;
        bottom: 45px;
    }
    padding-left: 200px;
    text-shadow: 0 1px 1px rgba(0, 0, 0, 0.4);

    h1 {
        @media (max-width: @screen-xs) {
            font-size: 25px;
        }
        font-size: 32px;
        font-weight: 200;
        letter-spacing: 1px;
        padding: 0;
    }

    h1 a {
        .truncateLines(24px, 32px, 1, true);
    }
}

.profiles_action {
    position: absolute;
    right: 0;
    top: 0;

    > ul {
        > li:hover {
            background: @brand-primary;
            color: @brand-primary-color;

            > ul {
                display: block;
            }
        }

        > li {
            float: left;
            display: block;
            position: relative;
            > a, > a:hover {
                display: block;
                height: @header-height;
                line-height: @header-height;
                font-size: 20px;
                padding: 0 10px;
                border-left: 1px darken(@header-bg, 8%) solid;

                i {
                    color: darken(@header-color, 4%);
                }

                span {
                    display: none;
                }
            }

            > a:hover {
                i {
                    color: @brand-primary-color;
                }
            }
        }

        > li > ul {
            margin: 0;
        }
    }
}

.pages_view_sub_menu {
    border: 0;
    font-size: @font-size-base - 1;
    padding: 0;

    a, a:hover {
        color: darken(@block-bg, 30%);
    }

    a:hover {
        color: @link-color;
        transition: @transition;
    }
}

