}

.search_result_photo {
    padding-top: 8px;
}

.main_search_bar_input {
    width: 300px;
}

.main_search_bar_button {
    background: url('@{image-folder}layout/main_search_bar_button.png') no-repeat;
    width: 25px;
    height: 25px;
    border: 0;
    padding: 0;
    vertical-align: top;
    overflow: hidden;
    text-indent: -1000px;
    cursor: pointer;
}
#welcome {
    padding: 0;
    margin-bottom: 20px;
}

.blog-category {
    padding: 10px;
    font-size: @font-size-base - 3;
    color: @block-color;
    display: inline-block;
    background: darken(@block-bg, 4%);
    margin-bottom: @block-content-padding;
