    }

    .music_row_content {
        padding-left: @block-content-padding + 120;
        header {
            h1 {
                margin: 0;
                padding: 0;
                line-height: normal;

                a {
                    display: block;
                    white-space: nowrap;
                }
            }

            h2 {
                margin: 0;
                a, a:hover {
                    font-size: @font-size-base - 2 !important;
                    color: lighten(@block-color, 5%) !important;
                    font-weight: normal !important;
                }
