            width: 100%;
        }
    }

    .music_time {
        position: absolute;
        right: 10px;
        top: 10px;
        font-size: @font-size-base - 1;
        color: lighten(@block-color, 5%);
    }

    .music_song_stats {
        margin-top: 5px;
        position: relative;
        height: 25px;

        > a, > a:hover {
            display: inline-block;
            font-size: @font-size-base - 2;
            background: @block-bg;
            color: @block-color;
            height: 20px;
            line-height: 20px;
            padding: 0 6px 0 25px;
            position: relative;
            margin-right: 5px;
        }

        > a:hover:before {
            color: @brand-primary;
            transition: @transition;
        }

        > a:before {
            position: absolute;
            left: 7px;
            top: 3px;
            font-size: @font-size-base - 2 !important;
            color: @link-focus;
        }

        > a.do_like:before {
            .fa('\f164');
        }

        > a.do_comment:before {
            .fa('\f075');
        }

        > ul {
            position: absolute;
            right: 0;
            top: 0;

            > li {
                display: inline-block;
                position: relative;
                margin-left: 10px;
                font-size: @font-size-base - 1;

                a, a:hover {
                    display: block;
                    color: lighten(@text-color, 5%);
                }

                a:before {
                    padding-right: 5px;
                    font-size: @font-size-base - 2 !important;
                    color: lighten(@text-color, 15%);
                }
            }

            > li.total_plays {
                a:before {
                    .fa('\f04b');
                }
            }

            > li.total_likes {
                a:before {
                    .fa('\f164');
                }
            }

            > li.total_comments {
                a:before {
                    .fa('\f075');
                }
            }
        }
    }

    @media (max-width: @screen-sm) {
        .music_row_image {
            width: 40px;
            a._size__120 {
                width: 40px;
                height: 40px;
                span {
                    top: 10px;
                    font-size: 14px;
                }
            }
        }

        .music_row_content {
            padding-left: 40 + @block-content-padding;
        }
    }
}

